const invoiceData = $json.data;
const formatCurrency = (amount) => `$${amount.toLocaleString('en-US', { minimumFractionDigits: 2, maximumFractionDigits: 2 })}`;

let markdown = `# Invoice ${invoiceData.invoiceNumber}\n\n`;
markdown += `## Summary\n\n`;
markdown += `- **Invoice Number**: ${invoiceData.invoiceNumber}\n`;
markdown += `- **Vendor**: ${invoiceData.vendorName}\n`;
markdown += `- **Date**: ${invoiceData.invoiceDate}\n`;
markdown += `- **Total Amount**: ${formatCurrency(invoiceData.totalAmount)}\n\n`;
markdown += `## Line Items\n\n`;

if (invoiceData.lineItems && invoiceData.lineItems.length > 0) {
  markdown += `| Description | Quantity | Unit Price | Amount |\n`;
  markdown += `|-------------|----------|------------|--------|\n`;
  invoiceData.lineItems.forEach(item => {
    markdown += `| ${item.description} | ${item.quantity} | ${formatCurrency(item.unitPrice)} | ${formatCurrency(item.amount)} |\n`;
  });
}

markdown += `\n---\n*Document generated automatically from invoice PDF*\n`;

return {
  json: {
    markdown: markdown,
    documentTitle: `Invoice ${invoiceData.invoiceNumber} - ${invoiceData.vendorName}`,
    invoiceNumber: invoiceData.invoiceNumber,
    vendor: invoiceData.vendorName,
    total: invoiceData.totalAmount
  }
};
