const meeting = $input.first().json.data;
const fileName = $('Google Drive Trigger')?.item?.json?.name || 'Unknown';
const fileId = $('Google Drive Trigger')?.item?.json?.id || '';

// Format attendees
const attendees = meeting.attendees || [];
const attendeeList = attendees.map(a => a.name).join(', ') || 'Not specified';
const attendeeCount = attendees.length;

// Format discussion points
const discussions = meeting.keyDiscussionPoints || [];
const discussionList = discussions.map(d => `â€¢ **${d.topic}**: ${d.summary}`).join('\n') || 'None recorded';

// Format decisions
const decisions = meeting.decisionsMade || [];
const decisionList = decisions.map(d => `âœ“ ${d.decision}`).join('\n') || 'None recorded';

// Format action items
const actions = meeting.actionItems || [];
const actionList = actions.map(a => 
  `â–¡ ${a.task} â†’ ${a.owner} (Due: ${a.dueDate || 'TBD'}) [${a.priority || 'Medium'}]`
).join('\n') || 'None assigned';

// Count items
const actionCount = actions.length;
const decisionCount = decisions.length;
const highPriorityCount = actions.filter(a => a.priority === 'High').length;

return [{
  json: {
    ...meeting,
    fileName: fileName,
    fileId: fileId,
    attendeeList: attendeeList,
    attendeeCount: attendeeCount,
    discussionList: discussionList,
    decisionList: decisionList,
    decisionCount: decisionCount,
    actionList: actionList,
    actionCount: actionCount,
    highPriorityCount: highPriorityCount,
    processedAt: new Date().toISOString()
  }
}];

