// Split multiple attachments into separate items
const message = $input.first();
const attachments = [];

// Find all binary attachments
for (const key in message.binary) {
  if (key.startsWith('attachment_') || key.startsWith('data')) {
    attachments.push({
      json: {
        attachmentFileName: message.binary[key].fileName,
        messageId: message.json.id,
        messageSubject: message.json.subject || 'No subject'
      },
      binary: {
        data: message.binary[key]
      }
    });
  }
}

// Return all attachments as separate items
return attachments.length > 0 ? attachments : [{
  json: { error: 'No attachments found' }
}];

const input = $input.first();
const doc = input.json.data;

// Get binary data from the original Split Attachments node
// (PDF Vector doesn't pass binary through)
const originalBinary = $('Split Attachments').item.binary;

// Categorize by form type - using .includes() for flexible matching
const formType = doc.formType || '';
const categories = {
  'W-2': 'Income_Statements',
  '1099': 'Income_Statements',
  'Receipt': 'Deductions',
  'Invoice': 'Deductions',
  '1098': 'Deductions',
  '1040': 'Tax_Returns'
};

// Find matching category
let category = 'Other';
for (const [key, value] of Object.entries(categories)) {
  if (formType.includes(key)) {
    category = value;
    break;
  }
}

const year = doc.taxYear || new Date().getFullYear();
const folderPath = `Tax_Documents/${year}/${category}`;

return [{
  json: {
    document: doc,
    category: category,
    taxYear: year,
    folderPath: folderPath,
    fileName: $('Split Attachments').item.json.attachmentFileName
  },
  binary: originalBinary  // Use binary from Split Attachments node!
}];
