const extractNode = $('PDF Vector - Extract Content').item.json;
const doc = extractNode.data || {};
const searchResult = $input.first().json;
const papers = searchResult.results || [];
const driveFile = $('Google Drive Trigger').item.json;

// Sort papers by citation count (highest first)
const topPapers = papers
  .filter(p => p && p.title)
  .sort((a, b) => (b.citationCount || b.totalCitations || 0) - (a.citationCount || a.totalCitations || 0))
  .slice(0, 10);

// Generate formatted reading list
const readingList = topPapers.map((p, i) => {
  const authors = p.authors ? p.authors.slice(0, 2).map(a => a.name || 'Unknown').join(', ') : 'Unknown';
  const citations = p.citationCount || p.totalCitations || 0;
  return `${i + 1}. ${p.title} (${p.year || 'N/A'}) - ${authors} - ${citations} citations`;
}).join('\n') || 'No papers found';

// Format research questions
const questions = doc.researchQuestions || [];
const questionsList = questions.length > 0 
  ? questions.map((q, i) => `RQ${i + 1}: ${q}`).join('\n') 
  : 'None identified';

// Combine and deduplicate search terms
const concepts = doc.keyConcepts || [];
const keywords = doc.keywords || [];
const searchTerms = [...new Set([...concepts, ...keywords])].slice(0, 10).join(', ') || 'N/A';

// Format main findings
const findings = doc.mainFindings || [];
const findingsList = findings.length > 0
  ? findings.map((f, i) => `‚Ä¢ ${f}`).join('\n')
  : 'None identified';

return [{
  json: {
    title: doc.title || driveFile.name || 'Untitled',
    author: doc.author || 'Unknown',
    institution: doc.institution || 'N/A',
    degreeType: doc.degreeType || 'N/A',
    chapterTitle: doc.chapterTitle || 'N/A',
    thesisStatement: doc.thesisStatement || 'N/A',
    methodology: doc.methodology || 'N/A',
    theoreticalFramework: doc.theoreticalFramework || 'N/A',
    keyConcepts: concepts.join(', ') || 'N/A',
    searchTerms: searchTerms,
    researchQuestions: questionsList,
    mainFindings: findingsList,
    readingList: readingList,
    paperCount: topPapers.length,
    topPapers: topPapers,
    fileName: driveFile.name || 'document',
    fileId: driveFile.id || '',
    processedAt: new Date().toISOString()
  }
}];

